    function encryptGedCode(code) {
        let alphabet, encrypted, i, n, rotated, rotation;
        alphabet = `NOPQRSTUVWXYZABCDEFGHIJKLM`;
        switch (Math.floor(Math.random() * 4)) {
            case 0:
                rotated = rot(code, 13);
                encrypted = ``;
                for (i = 0, n = rotated.length; i < n; ++i) {
                    encrypted += rotated.charCodeAt(i).toString(16);
                }
                return encrypted;
            case 1:
                rotated = rot(code, 13);
                encrypted = ``;
                for (i = 0, n = rotated.length; i < n; ++i) {
                    encrypted += rotated.charCodeAt(i).toString(16);
                }
                encrypted = encrypted.replace(/\d/g, n => {
                    return alphabet[parseInt(n)];
                });
                return encrypted;
            case 2:
                rotation = Math.ceil(Math.random() * 25);
                rotated = rot(code, rotation);
                encrypted = ``;
                for (i = 0, n = rotated.length; i < n; ++i) {
                    encrypted += rotated.charCodeAt(i).toString(16);
                }
                encrypted += rotation.toString(16);
                return encrypted;
            case 3:
                rotation = Math.ceil(Math.random() * 25);
                rotated = rot(code, rotation);
                encrypted = ``;
                for (i = 0, n = rotated.length; i < n; ++i) {
                    encrypted += rotated.charCodeAt(i).toString(16);
                }
                encrypted += rotation.toString(16);
                encrypted = encrypted.replace(/\d/g, n => {
                    return alphabet[parseInt(n)];
                });
                return encrypted;
        }
    }

    function decryptGedCode(encrypted) {
        let alphabet, code, rotation;
        alphabet = `NOPQRSTUVWXYZABCDEFGHIJKLM`;
        encrypted = encrypted.replace(/-/g, ``).replace(/[A-Z]/g, n => {
            return alphabet.indexOf(n);
        });
        rotation = encrypted.slice(10);
        if (rotation) {
            rotation = parseInt(rotation, 16);
        } else {
            rotation = 13;
        }
        code = ``;
        encrypted.slice(0, 10).match(/../g).forEach(n => {
            code += String.fromCharCode(parseInt(n, 16));
        });
        return rot(code, 26 - rotation);
    }

    async function saveGedGiveaways(context, source) {
        let codes = [];
        let elements = context.querySelectorAll(`[href^="ESGST-"]`);
        for (let i = 0, n = elements.length; i < n; i++) {
            let encryptedCode = elements[i].getAttribute(`href`).match(/ESGST-(.+)/)[1];
            if (!encryptedCode.match(/currentVersion/)) {
                codes.push(decryptGedCode(encryptedCode));
            }
        }
        if (!codes.length) {
            return;
        }
        let ged = {
            decryptedGiveaways: await getValue(`decryptedGiveaways`, await getValue(`exclusiveGiveaways`, {})),
            giveaways: {}
        };
        if (typeof ged.decryptedGiveaways === `string`) {
            ged.decryptedGiveaways = JSON.parse(ged.decryptedGiveaways);
        }
        let promises = [];
        codes.forEach(code => {
            if (ged.decryptedGiveaways[code]) {
                return;
            }
            let giveaway = esgst.giveaways[code];
            if (giveaway && giveaway.endTime) {
                ged.decryptedGiveaways[code] = {
                    source: source,
                    timestamp: giveaway.endTime
                };
                return;
            }
            promises.push(getGedGiveaway(code, ged.giveaways, ged.decryptedGiveaways, false, source));
        });
        await Promise.all(promises);
        await setValue(`decryptedGiveaways`, JSON.stringify(ged.decryptedGiveaways));
        await lockAndSaveGiveaways(ged.giveaways);
    }

