    function getHrDefaultSound() {
        return ``;
    }

    async function createHrPlayer(string) {
        let binary = atob(string);
        let buffer = new ArrayBuffer(binary.length);
        let bytes = new Uint8Array(buffer);
        for (let i = buffer.byteLength - 1; i > -1; i--) {
            bytes[i] = binary.charCodeAt(i) & 0xFF;
        }
        return {
            play: playHrSound.bind(null, await esgst.audioContext.decodeAudioData(buffer))
        }
    }

    function playHrSound(buffer) {
        let source = esgst.audioContext.createBufferSource();
        source.buffer = buffer;
        source.loop = false;
        source.connect(esgst.audioContext.destination);
        source.start(0);
    }

