    function convertBytes(bytes) {
        if (bytes < 1024) {
            return `${bytes} B`;
        } else {
            bytes /= 1024;
            if (bytes < 1024) {
                return `${Math.round(bytes * 100) / 100} KB`;
            } else {
                return `${Math.round(bytes / 1024 * 100) / 100} MB`;
            }
        }
    }

