function getRemainingTime(time) {
    var d, dif, h, m, s, w;
    dif = time - Date.now();
    if (dif < 0) {
        dif *= -1;
    }
    w = Math.floor(dif / 604800000);
    if (w > 0) {
        return `${w}w`;
    } else {
        d = Math.floor(dif / 86400000);
        if (d > 0) {
            return `${d}d`;
        } else {
            h = Math.floor(dif / 3600000);
            if (h > 0) {
                return `${h}h`;
            } else {
                m = Math.floor(dif / 60000);
                if (m > 0) {
                    return `${m}m`;
                } else {
                    s = Math.floor(dif / 1000);
                    return `${s}s`;
                }
            }
        }
    }
}

