async function loadImportFile(dm, storageType, space, callback) {
  switch (storageType) {
    case STORAGE_TYPES.DROPBOX:
      CloudStorage_CloudStorage.manage(CloudStorage_CloudStorage.DROPBOX, null, dm, callback);
      break;

    case STORAGE_TYPES.GOOGLE_DRIVE:
      CloudStorage_CloudStorage.manage(CloudStorage_CloudStorage.GOOGLEDRIVE, null, dm, callback);
      break;

    case STORAGE_TYPES.ONEDRIVE:
      CloudStorage_CloudStorage.manage(CloudStorage_CloudStorage.ONEDRIVE, null, dm, callback);
      break;

    case STORAGE_TYPES.COMPUTER:
      {
        let fileField, warningNode;
        const popup = new Popup_Popup({
          isTemp: true
        });
        popup.setScrollable(class_DOM_DOM.element("fragment", null, class_DOM_DOM.element("input", {
          type: "file",
          ref: ref => fileField = ref
        }), class_DOM_DOM.element("div", {
          className: "esgst-description esgst-warning",
          ref: ref => warningNode = ref
        })));
        popup.onCloseByUser = callback;

        const cb = () => {
          popup.close();
          callback();
        };

        fileField.addEventListener('change', () => {
          const file = fileField.files[0];

          if (file) {
            dm.reader = new FileReader();
            const isZip = file.name.match(/\.zip$/);

            dm.reader.onload = async () => {
              try {
                await readImportFile(dm, STORAGE_TYPES.COMPUTER, space, isZip, cb);
              } catch (err) {
                Shared.common.createFadeMessage(warningNode, 'Cannot parse file!');
              }
            };

            if (isZip) {
              dm.reader.readAsBinaryString(file);
            } else {
              dm.reader.readAsText(file);
            }
          } else {
            Shared.common.createFadeMessage(warningNode, 'No file was loaded!');
          }
        });
        popup.open();
        break;
      }

    case STORAGE_TYPES.TEXT:
      {
        let dataField, warningNode;
        const popup = new Popup_Popup({
          isTemp: true
        });
        class_DOM_DOM.insert(popup.description, 'beforeend', class_DOM_DOM.element("fragment", null, class_DOM_DOM.element("textarea", {
          className: "esgst-textarea-large",
          style: {
            width: '900px'
          },
          ref: ref => dataField = ref
        }), class_DOM_DOM.element("div", {
          className: "esgst-description esgst-warning",
          ref: ref => warningNode = ref
        })));
        popup.onCloseByUser = callback;

        const cb = () => {
          popup.close();
          callback();
        };

        Button_Button.create({
          template: 'success',
          name: 'Restore',
          onClick: () => {
            const data = dataField.value;

            if (data) {
              try {
                dm.data = JSON.parse(data);
                Shared.common.createConfirmation('Are you sure you want to restore the selected data?', manageData.bind(null, dm, storageType, space, cb), cb);
              } catch (err) {
                Shared.common.createFadeMessage(warningNode, 'Cannot parse data! Data should be in JSON format.');
              }
            } else {
              Shared.common.createFadeMessage(warningNode, 'No data was provided!');
            }
          }
        }).insert(popup.description, 'beforeend');
        popup.open();
        break;
      }
  }
}

async function readImportFile(dm, storageType, space, isZip, callback) {
  if (dm.reader) {
    dm.data = JSON.parse(isZip ? (await Shared.common.readZip(dm.reader.result))[0].value : dm.reader.result);
  }

  Shared.common.createConfirmation('Are you sure you want to restore the selected data?', manageData.bind(null, dm, storageType, space, callback), callback);
}

