  getFeaturesById() {
    const featuresById = {};
    const featuresAncestors = {};

    for (const type in Shared.esgst.features) {
      for (const id in Shared.esgst.features[type].features) {
        const feature = Shared.esgst.features[type].features[id];
        this.getFeatureById(feature, id, featuresById, featuresAncestors);
      }
    }

    for (const [id, feature] of Object.entries(featuresById)) {
      if (!feature.alias) {
        continue;
      }

      const aliasFeature = featuresById[feature.alias];

      for (const key in aliasFeature) {
        feature[key] = aliasFeature[key];
      }

      feature.id = id;
    }

    return [featuresById, featuresAncestors];
  }

  getFeatureById(feature, id, featuresById, featuresAncestors) {
    feature.id = id;
    featuresById[id] = feature;

    if (feature.features) {
      for (const subId in feature.features) {
        const subFeature = feature.features[subId];
        featuresAncestors[subId] = id;
        this.getFeatureById(subFeature, subId, featuresById, featuresAncestors);
      }
    }
  }

  checkBusy(event) {
    if (document.getElementsByClassName('esgst-busy')[0] || this.esgst.busy) {
      event.returnValue = true;
      return true;
    }
  }

  setMouseEvent(element, id, url, callback) {
    let isDragging = -1;
    let startingPos = [0, 0];
    element.addEventListener('mousedown', event => {
      if (event.button === 2) return; // right click, do nothing

      if (event.button === 1) {
        // middle click
        event.preventDefault();
      }

      isDragging = event.button;
      startingPos = [event.pageX, event.pageY];
    });
    element.addEventListener('mousemove', event => {
      if (isDragging === -1 || event.pageX === startingPos[0] && event.pageY === startingPos[1]) return;
      isDragging = -1;
    });
    element.addEventListener('mouseup', () => {
      if (isDragging === -1) return;

      if (Settings.get(id) || isDragging === 1) {
        Tabs.open(url);
      } else {
        callback();
      }

      isDragging = -1;
      startingPos = [0, 0];
    });
  }

  createHeadingButton(details) {
    let context = details.context;
    const id = details.orderId || details.id;

    if (!context) {
      if (id === 'gmf' && Shared.esgst.discussionPath) {
        context = document.querySelector('.page__heading__breadcrumbs');
        context.parentElement.insertBefore(details.element, context);
        return context.previousElementSibling;
      } else if (Settings.get('leftButtonIds').indexOf(id) > -1) {
        context = this.esgst.leftButtons;
      } else if (Settings.get('rightButtonIds').indexOf(id) > -1) {
        context = this.esgst.rightButtons;
      } else if (Settings.get('leftMainPageHeadingIds').indexOf(id) > -1) {
        context = this.esgst.leftMainPageHeadingButtons;
      } else {
        context = this.esgst.rightMainPageHeadingButtons;
      }
    }

    if (details.element) {
      context.appendChild(details.element);
      return context.lastElementChild;
    }

    const children = [];

    if (details.isSwitch) {
      children.push({
        type: 'span'
      });
    }

    for (const icon of details.icons) {
      children.push({
        attributes: {
          class: `fa ${icon}`
        },
        type: 'i'
      });
    }

    const attributes = {
      class: 'esgst-heading-button',
      ['data-draggable-id']: id,
      id: `esgst-${details.id}`,
      title: this.getFeatureTooltip(details.featureId || details.id, details.title)
    };

    if (details.link) {
      attributes.href = details.link;
      attributes.target = '_blank';
    }

    return this.createElements(context, 'beforeend', [{
      attributes,
      type: details.link ? 'a' : 'div',
      children
    }]);
  }

  async checkNewVersion() {
    if (Shared.esgst.isFirstRun) {
      // noinspection JSIgnoredPromiseFromCall
      this.setSetting('dismissedOptions', this.esgst.toDismiss);
      const popup = new Popup_Popup({
        addScrollable: true,
        icon: 'fa-smile-o',
        isTemp: true,
        title: class_DOM_DOM.element("fragment", null, class_DOM_DOM.element("i", {
          className: "fa fa-circle-o-notch fa-spin"
        }), " Hi! ESGST is retrieving your avatar, username and Steam ID. This will not take long...")
      });
      popup.open();
      await this.checkSync(true);
      popup.close();
      SettingsWizard.run();
      Shared.esgst.isFirstRun = false;
    } else if (Shared.esgst.isUpdate && Settings.get('showChangelog')) {
      const hasPermission = await Permissions_permissions.contains([['github']]);

      if (!hasPermission) {
        new Popup_Popup({
          addScrollable: true,
          icon: 'fa-check',
          isTemp: true,
          title: class_DOM_DOM.element("fragment", null, "ESGST has updated from v", Shared.esgst.previousVersion, " to v", Shared.esgst.currentVersion, "! Please go to", ' ', class_DOM_DOM.element("a", {
            href: "https://github.com/rafaelgssa/esgst/-/releases"
          }, "https://github.com/rafaelgssa/esgst/-/releases"), ' ', "to view the changelog. If you want the changelog to be automatically retrieved from GitHub and shown in this popup when updating, then go to the settings menu and grant permission to \"github.com\"")
        }).open();
        Shared.esgst.isUpdate = false;
        return;
      }

      const popup = new Popup_Popup({
        addScrollable: true,
        icon: 'fa-circle-o-notch fa-spin',
        isTemp: true,
        title: `ESGST has updated from v${Shared.esgst.previousVersion} to v${Shared.esgst.currentVersion}! Please wait while the changelog is retrieved from GitHub.`
      });
      popup.open();

      try {
        let changelog = '';
        const refsResponse = await FetchRequest_FetchRequest.get('https://api.github.com/repos/rafaelgssa/esgst/git/matching-refs/tags');

        if (!refsResponse || !refsResponse.json) {
          return;
        }

        const refs = refsResponse.json;
        refs.reverse();
        let currentIndex = refs.findIndex(ref => ref.ref === `refs/tags/v${Shared.esgst.currentVersion}`);
        const previousIndex = refs.findIndex(ref => ref.ref === `refs/tags/v${Shared.esgst.previousVersion}`);

        if (currentIndex > -1 && previousIndex > -1) {
          while (currentIndex < previousIndex) {
            const version = refs[currentIndex].ref.split('/tags/')[1];
            const releaseResponse = await FetchRequest_FetchRequest.get(`https://api.github.com/repos/rafaelgssa/esgst/releases/tags/${version}`);

            if (releaseResponse && releaseResponse.json) {
              changelog = `${changelog}## ${version}\n\n${releaseResponse.json.body.replace(/#(\d+)/g, '[$1](https://github.com/rafaelgssa/esgst/issues/$1)')}\n\n`;
            }

            currentIndex += 1;
          }
        }

        popup.setIcon('fa-star');
        popup.setTitle(`ESGST has updated from v${Shared.esgst.previousVersion} to v${Shared.esgst.currentVersion}! Here's the changelog:`);
        popup.getScrollable(class_DOM_DOM.element("div", {
          className: "markdown"
        }, await this.parseMarkdown(popup.scrollable, changelog)));
      } catch (e) {
        class_Logger_Logger.warning(e.message);
        popup.setIcon('fa-times');
        popup.setTitle(class_DOM_DOM.element("fragment", null, "ESGST has updated from v", Shared.esgst.previousVersion, " to v", Shared.esgst.currentVersion, "! An error occurred when retrieving the changelog from GitHub, please go to", ' ', class_DOM_DOM.element("a", {
          href: "https://github.com/rafaelgssa/esgst/releases"
        }, "https://github.com/rafaelgssa/esgst/releases"), ' ', "to view it."));
      }

      Shared.esgst.isUpdate = false;
    }
  }

