  continueRequest(details) {
    return new Promise(async (resolve, reject) => {
      let isLocal = details.url.match(/^\//) || details.url.match(new RegExp(window.location.hostname));
      details.url = details.url.replace(/^\//, `https://${window.location.hostname}/`).replace(/^https?:/, Shared.esgst.locationHref.match(/^http:/) ? 'http:' : 'https:');

      if (isLocal) {
        const requestOptions = {
          body: details.data,
          credentials:
          /** @type {"omit"|"include"} */
          details.anon ? 'omit' : 'include',
          headers: details.headers,
          method: details.method,
          redirect: 'follow'
        };
        let response = null;
        let responseText = null;

        try {
          let _fetch;

          let _requestOptions;

          if ((await this.getBrowserInfo()).name === 'Firefox' && Utils.isSet(window.wrappedJSObject)) {
            // @ts-ignore
            _fetch = XPCNativeWrapper(window.wrappedJSObject.fetch); // @ts-ignore

            window.wrappedJSObject.requestOptions = cloneInto(requestOptions, window); // @ts-ignore

            _requestOptions = XPCNativeWrapper(window.wrappedJSObject.requestOptions);
          } else {
            _fetch = window.fetch;
            _requestOptions = requestOptions;
          }

          response = await _fetch(details.url, _requestOptions);
          responseText = await response.text();

          if (!response.ok) {
            throw responseText;
          }
        } catch (error) {
          reject({
            error
          });
          return;
        }

        response = {
          finalUrl: response.url,
          redirected: response.redirected,
          responseText
        };
        resolve(response);

        if (response.finalUrl.match(/www.steamgifts.com/)) {
          this.lookForPopups(response);
        }
      } else {
        const manipulateCookies = (await this.getBrowserInfo()).name === 'Firefox' && Settings.get('manipulateCookies');
        _browser.runtime.sendMessage({
          action: 'fetch',
          blob: details.blob,
          fileName: details.fileName,
          manipulateCookies,
          parameters: JSON.stringify({
            body: details.data,
            credentials: details.anon || manipulateCookies ? 'omit' : 'include',
            headers: details.headers,
            method: details.method,
            redirect: 'follow'
          }),
          url: details.url
        }).then(response => {
          if (typeof response === 'string') {
            response = JSON.parse(response);
          }

          if (Utils.isSet(response.error)) {
            reject(response);
            return;
          }

          resolve(response);

          if (response.finalUrl.match(/www.steamgifts.com/)) {
            this.lookForPopups(response);
          }
        });
      }
    });
  }

